mapper.update:
UPDATE T_UDMP_BATCH_JOB_RUN
IS_DELETED = 1, UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP ,
WHERE JOB_RUN_ID = #{job_run_id}

UPDATE T_UDMP_BATCH_JOB_RUN
SUCCESS_CNT = #{success_cnt, jdbcType=NUMERIC} , TOTAL_CNT = #{total_cnt, jdbcType=NUMERIC} , FAILED_CNT = #{failed_cnt, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE , UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP ,
WHERE JOB_RUN_ID = #{job_run_id}

UPDATE T_UDMP_BATCH_JOB_RUN
STATUS = #{status, jdbcType=VARCHAR} , UPDATE_TIME = SYSDATE , UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP ,
WHERE JOB_RUN_ID = #{job_run_id}
